import React, { useEffect, useState } from "react";
import Axios from "axios";
import { Card, Col, Row } from "antd";

const ShowEmployee = (props) => {
  const [empData, setEmpData] = useState({});
  useEffect(() => {
    Axios.get(`/employees/${props.empid}`).then((res) => {
      setEmpData(res.data);
    });
  }, []);

  return (
    <div>
      <h1>Show {props.empid}</h1>
      <Card title="Employee Details" bordered={true} className="my-3">
        <Row>
          <Col>
            <p><b>Emp Id: </b>{empData.EMPID}</p>
            <p><b>Name: </b>{empData.FIRSTNAME + empData.LASTNAME}</p>
            <p><b>Email: </b>{empData.EMAIL}</p>
            <p><b>Address: </b>{empData.CURRENTLOCATION && empData.CURRENTLOCATION.ADDRESS}</p>
            <p><b>Unit: </b>{empData.UNIT}</p>
            <p><b>Status: </b>{empData.STATUS}</p>
            <p><b>Date of Joining: </b>{empData.DOJ}</p>
            <p><b>Current Project: </b>{empData.PROJECTS && empData.PROJECTS.NAME}</p>
          </Col>
        </Row>
      </Card>
    </div>
  );
};

export default ShowEmployee;
