import React, { useEffect, useState } from "react";
import Axios from "axios";
import { useAlert } from "react-alert";
import { Form, Card, Button, Tabs, DatePicker, Input, Select, message } from "antd";
import { editEmployee } from "../../../actions/employee/employeeActions";
import { connect } from "react-redux";
import { Row, Col } from "antd";
import moment from "moment";
import { Spin } from "antd";
const { TabPane } = Tabs;
const { Option } = Select;

const EditEmployee = (props) => {
  const [empData, setEmpData] = useState({});
  const [locationArray, setLocationArry] = useState(props.location);
  const [projectArray, setProjectArry] = useState(props.projects);
  const [loading, setLoading] = useState(true);
  const [spin, setSpin] = useState(false);
  const alert = useAlert;

  useEffect(() => {
    Axios.get(`/employees/${props.empid}`).then((res) => {
      setEmpData(res.data);
      setLoading(false);
    });
    localStorage.setItem("EmpID", props.empid);
  }, []);
  useEffect(() => {
    if (props.employee.errors) {
      // message.warning(props.employee.errors);
      setSpin(false);
    }
  }, [props.employee.errors]);

  const layout = {
    labelCol: {
      span: 4,
    },
    wrapperCol: {
      span: 24,
    },
  };
  const onFinish = (values) => {
    const newEmp = {
      ...values,
      DOB: moment(values.DOB).format("YYYY-MM-DD"),
      DOJ: moment(values.DOJ).format("YYYY-MM-DD"),
      LastWorkingDate: moment(values.LastWorkingDate).format("YYYY-MM-DD"),
    };
    console.log("Success:", newEmp);
    // setSpin(true);
    // props.editEmployee(newEmp);
  };

  const onFinishFailed = (errorInfo) => {
    console.log(">>>>>Failed>>>>:", errorInfo);
    message.warning('"fill Presonal & Business Details currectly"');
  };
  const [form] = Form.useForm();

  return (
    <div>
      <h2>Edit Employee Details : {props.empid}</h2>
      <Spin spinning={spin} size="large">
        <Card
          title={empData.FIRSTNAME && empData.FIRSTNAME + " " + empData.LASTNAME}
          bordered={true}
          className="my-3"
          loading={loading}
        >
          <Form
            {...layout}
            size="middle"
            name="basic"
            form={form}
            onFinish={onFinish}
            onFinishFailed={onFinishFailed}
            initialValues={{
              FIRSTNAME: empData.FIRSTNAME,
              MIDDLENAME: empData.MIDDLENAME,
              LASTNAME: empData.LASTNAME,
              GENDER: empData.GENDER,
              MOBILENUMBER: empData.MOBILENUMBER,
              CAREERTRACK: empData.CAREERTRACK,
              EMPTYPE: empData.EMPTYPE,
              DOB: moment(empData.DOB),
              DOJ: moment(empData.DOJ),
              DESIGNATION: empData.DESIGNATION,
              STATUS: empData.STATUS,
              UNIT: empData.UNIT,
              DEPARTMENT: empData.DEPARTMENT,
              DEPARTMENT: empData.DEPARTMENT,
              MANAGER: empData.MANAGER,
              SEATID: empData.SEATID,
              CURRENTLOCATION: empData.CURRENTLOCATION && empData.CURRENTLOCATION.ID,
              BASELOCATION: empData.BASELOCATION && empData.BASELOCATION.ID,
              PROJECTS: empData.PROJECTS && empData.PROJECTS.ID,
              EXTENSIONNUMBER: empData.EXTENSIONNUMBER,
              LastWorkingDate:null
            }}
          >
            <Tabs defaultActiveKey="1">
              <TabPane tab="Personal Details" key="1">
                <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                  <Col className="gutter-row" span={12}>
                    <label>First Name </label>
                    <Form.Item
                      name="FIRSTNAME"
                      hasFeedback
                      rules={[
                        { required: true, message: "Please input Firstname!" },
                      ]}
                    >
                      <Input placeholder="firstname" />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Middle Name </label>
                    <Form.Item
                      name="MIDDLENAME"
                      hasFeedback
                      rules={[
                        { required: true, message: "Please input Middlename!" },
                      ]}
                    >
                      <Input placeholder="middlename" />
                    </Form.Item>
                  </Col>

                  <Col className="gutter-row" span={12}>
                    <label>Last Name </label>
                    <Form.Item
                      name="LASTNAME"
                      hasFeedback
                      rules={[{ required: true, message: "Please input Lastname!" }]}
                    >
                      <Input placeholder="lastname" />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Gender</label>
                    <Form.Item
                      name="GENDER"
                      hasFeedback
                      rules={[
                        {
                          required: true,
                          message: "Please select Gender!",
                        },
                      ]}
                    >
                      <Select placeholder="Select Gender">
                        <Option value="male">Male</Option>
                        <Option value="female">Female</Option>
                      </Select>
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Mobile</label>
                    <Form.Item
                      name="MOBILENUMBER"
                      hasFeedback
                      rules={[
                        { required: true, message: "Please enter Mobile Number!" },
                        { min: 10, message: "Minimum length should be 10" },
                      ]}
                    >
                      <Input type="number" autoComplete="off" />
                    </Form.Item>
                  </Col>

                  <Col className="gutter-row" span={12}>
                    <label>DOB</label>
                    <Form.Item
                      name="DOB"
                      rules={[
                        {
                          type: "object",
                          required: true,
                          message: "Please input your DOB",
                        },
                      ]}
                    >
                      <DatePicker />
                    </Form.Item>
                  </Col>
                </Row>
              </TabPane>

              <TabPane tab="Business Details" key="2">
                <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                  <Col className="gutter-row" span={12}>
                    <label>Careertrack</label>
                    <Form.Item
                      name="CAREERTRACK"
                      hasFeedback
                      rules={[
                        { required: true, message: "Please enter Careertrack!" },
                      ]}
                    >
                      <Input placeholder="careertrack" />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Departname </label>
                    <Form.Item
                      name="DEPARTMENT"
                      hasFeedback
                      rules={[
                        { required: true, message: "Please input Departname!" },
                      ]}
                    >
                      <Input placeholder="Departname" />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Manager </label>
                    <Form.Item
                      name="MANAGER"
                      hasFeedback
                      rules={[{ required: true, message: "Please enter Manager!" }]}
                    >
                      <Input placeholder="Manager" />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>EMP Type</label>
                    <Form.Item
                      name="EMPTYPE"
                      hasFeedback
                      rules={[
                        {
                          required: true,
                          message: "Please select Employee Type!",
                        },
                      ]}
                    >
                      <Select placeholder="Select Employee Type">
                        <Option value="regular">Regular</Option>
                        <Option value="contractor">Contractor</Option>
                        <Option value="intern">Intern</Option>
                      </Select>
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Designation </label>
                    <Form.Item
                      name="DESIGNATION"
                      hasFeedback
                      rules={[
                        {
                          required: true,
                          message: "Please select Designation!",
                        },
                      ]}
                    >
                      <Select placeholder="Select Designation">
                        <Option value="engineer">Engineer</Option>
                        <Option value="lead">Lead</Option>
                        <Option value="manager">Manager</Option>
                      </Select>
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Status </label>
                    <Form.Item
                      name="STATUS"
                      hasFeedback
                      rules={[
                        {
                          required: true,
                          message: "Please select Status!",
                        },
                      ]}
                    >
                      {/* <Select placeholder="Select Status">
                      <Option value="onboarding">Onboarding</Option>
                      <Option value="offboarding">Offboarding</Option>
                      <Option value="inactive">Inactive</Option>
                    </Select> */}
                      <Input placeholder="Status" />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>UNIT </label>
                    <Form.Item
                      name="UNIT"
                      hasFeedback
                      rules={[
                        {
                          required: true,
                          message: "Please select Unit!",
                        },
                      ]}
                    >
                      <Select placeholder="Select Unit">
                        <Option value="cis">CIS</Option>
                        <Option value="idm">IDM</Option>
                        <Option value="other">Other</Option>
                      </Select>
                    </Form.Item>
                  </Col>

                  <Col className="gutter-row" span={12}>
                    <label>Project </label>
                    <Form.Item
                      name="PROJECTS"
                      hasFeedback
                      rules={[
                        {
                          required: true,
                          message: "Please select Project!",
                        },
                      ]}
                    >
                      <Select placeholder="Select project">
                        {projectArray &&
                          projectArray.map((data) => (
                            <Option value={data.ID} key={data.ID}>
                              {data.NAME ? data.NAME : ""}
                            </Option>
                          ))}
                      </Select>
                    </Form.Item>
                  </Col>

                  <Col className="gutter-row" span={12}>
                    <label>Base Location </label>
                    <Form.Item
                      name="BASELOCATION"
                      hasFeedback
                      rules={[
                        {
                          required: true,
                          message: "Please select Base Location!",
                        },
                      ]}
                    >
                      <Select placeholder="Select Base Location">
                        {locationArray &&
                          locationArray.map((data) => (
                            <Option value={data.ID} key={data.ID}>
                              {data.ADDRESS ? data.ADDRESS : ""}
                            </Option>
                          ))}
                      </Select>
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Current Location </label>
                    <Form.Item
                      name="CURRENTLOCATION"
                      hasFeedback
                      rules={[
                        {
                          required: true,
                          message: "Please select Current Location!",
                        },
                      ]}
                    >
                      <Select placeholder="Select Current Location">
                        {locationArray &&
                          locationArray.map((data) => (
                            <Option value={data.ID} key={data.ID}>
                              {data.ADDRESS ? data.ADDRESS : ""}
                            </Option>
                          ))}
                      </Select>
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Seat No </label>
                    <Form.Item
                      name="SEATID"
                      hasFeedback
                      rules={[{ required: true, message: "Please input Seat No!" }]}
                    >
                      <Input placeholder="Seat No" />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Extension No </label>
                    <Form.Item
                      name="EXTENSIONNUMBER"
                      hasFeedback
                      rules={[
                        { required: true, message: "Please input Extension No!" },
                      ]}
                    >
                      <Input placeholder="Extension No" />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>DOJ</label>
                    <Form.Item
                      name="DOJ"
                      rules={[
                        {
                          type: "object",
                          required: true,
                          message: "Please input your DOJ",
                        },
                      ]}
                    >
                      <DatePicker />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Last Working Date</label>
                    <Form.Item
                      name="LastWorkingDate"
                      rules={[
                        {
                          type: "object",
                          // required: true,
                          message: "Please input your Last Working Date",
                        },
                      ]}
                    >
                      <DatePicker />
                    </Form.Item>
                  </Col>
                </Row>
              </TabPane>
            </Tabs>
            <Form.Item className="m-1">
              <Button type="primary" htmlType="submit">
                Update
              </Button>
            </Form.Item>
          </Form>
        </Card>
      </Spin>
    </div>
  );
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  messages: state.messages,
  employee: state.employee,
});
export default connect(mapStateToProps, { editEmployee })(EditEmployee);
