import { put, takeLatest } from "redux-saga/effects";
import {
  SET_LOADING
} from "../actions/employee/employeeTypes";
import {
  LOGIN,
  LOGOUT,
  LOGIN_SUCCESS,
  LOGOUT_SUCCESS,
  CLEAR_MESSAGES,
  CURRENT_USER,
  SUCCESS_MESSAGE,
  ERROR_MESSAGE
} from "../actions/auth/authTypes";
import Axios from "axios";

function* userLogin({ payload }) {
   yield put({ type: CLEAR_MESSAGES });
   yield put({ type: SET_LOADING });

   try {

      const user = yield Axios.post( "/api/token/",payload)
                              .then((res) => {
                                 console.log(res);
                                 return res.data;
                              })
                              .catch(err => {
                                 console.log(err);
                              });
      console.log(user);
      let msg;
      if (user) {
         localStorage.setItem('accessToken', user.access);
         localStorage.setItem('refreshToken', user.refresh);
         const userData = {
            user:payload.username
         }
         yield put({ type: LOGIN_SUCCESS, payload: userData });
         localStorage.setItem("user", JSON.stringify(userData));
         msg = {
            id: 1,
            text: "Login successful"
         };
         yield put({ type: SUCCESS_MESSAGE, payload: msg });
      }else{
         msg = {
            id: 1,
            text: "No active account found with the given credentials"
         };
         yield put({ type: ERROR_MESSAGE, payload: msg });
      }


   } catch (e) {
      console.log(e);
   }
   yield put({ type: SET_LOADING });
}
function* userLogout() {
  yield put({ type: CLEAR_MESSAGES });
  // yield put({ type: SET_LOADING });
  // try {
  //    const user = yield fetch(`https://`).then((response) => response.json());

  //    yield put({ type: LOGOUT_SUCCESS, payload: user });
  // } catch (e) {
  //    yield put({ type: LOGOUT_FAIL, payload: e.message });
  // }
  // yield put({ type: SET_LOADING });
  const successMsg = {
    id: 1,
    text: "Logout successful",
  };
  localStorage.removeItem("user");
  localStorage.removeItem("accessToken");
  localStorage.removeItem("refreshToken");
  yield put({ type: LOGOUT_SUCCESS });
  yield put({ type: SUCCESS_MESSAGE, payload: successMsg });
}
function* currentUser({ payload }) {
   yield put({ type: LOGIN_SUCCESS,  payload: payload });
}

export function* authActionWatcher() {
  yield takeLatest(LOGIN, userLogin);
  yield takeLatest(LOGOUT, userLogout);
  yield takeLatest(CURRENT_USER, currentUser);
}
