import React, { useState, useEffect } from "react";
import { Form, Input, Button, Card, Col, Row } from "antd";
import { UserOutlined, LockOutlined } from "@ant-design/icons";
import "./login.scss";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { userLogin } from "../../../actions/auth/authActions";
import { withRouter } from "react-router-dom";

const Login = (props) => {
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");

  useEffect(() => {
     if (props.auth.isAuthenticated) {
        props.history.push('/dashboard');
     }
  }, [props.auth])

  const loginHandler = (values) => {
    const user = {
      username: name,
      password: password,
    };
    props.userLogin(user);
  };

  const cardTitle = (
    <span className="d-flex justify-content-center" style={{ fontSize: "25px" }}>
      HR Portal
    </span>
  );

  return (
    <div className="container login">
      <Row justify="center">
        <Col sm={16} md={12} lg={8}>
          <Card title={cardTitle} bordered={true} className="my-3">
            <Form
              name="normal_login"
              className="login-form"
              initialValues={{ remember: true }}
              onFinish={loginHandler}
            >
              <Form.Item
                name="username"
                rules={[{ required: true, message: "Please input your Username!" }]}
              >
                <Input
                  prefix={<UserOutlined className="site-form-item-icon" />}
                  placeholder="Username"
                  size="large"
                  onChange={(e) => setName(e.target.value)}
                  value={name}
                />
              </Form.Item>
              <Form.Item
                name="password"
                rules={[{ required: true, message: "Please input your Password!" }]}
              >
                <Input.Password
                  prefix={<LockOutlined className="site-form-item-icon" />}
                  placeholder="Password"
                  size="large"
                  onChange={(e) => setPassword(e.target.value)}
                  value={password}
                />
              </Form.Item>
              <Form.Item>
                <Button
                  type="primary"
                  htmlType="submit"
                  className="login-form-button"
                  block
                >
                  Log in
                </Button>
              </Form.Item>
            </Form>
          </Card>
        </Col>
      </Row>
    </div>
  );
};
Login.propTypes = {
  userLogin: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired
};

const mapStateToProps = (state) => ({
   auth: state.auth
});

export default connect(mapStateToProps, { userLogin })(withRouter(Login));
