import React, { useState, useEffect } from "react";
import { Table, Input, Button, Select, Modal } from "antd";
import { connect } from "react-redux";
// import { Icon } from 'antd';
import Axios from "axios";
import BulkUpload from "../bulkupload/BulkUpload";
import AddEmployee from "../addemployee/AddEmployee";
import EditEmployee from "../editemployee/EditEmployee";
const { Option } = Select;
const { Search } = Input;

const Dashboard = (props) => {
  const paginationConfig = {
    defaultPageSize: 10,
    showSizeChanger: true,
    pageSizeOptions: ["10", "20", "50", "100"],
  };
  const [data, setData] = useState([]);
  const [pagination, setPagination] = useState(paginationConfig);
  const [loading, setLoading] = useState(false);
  const [searchInput, setSearchInput] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [selectModal, setSelectModal] = useState("");
  const [searchParams, setSearchParams] = useState({});

  useEffect(() => {
    fetch();
    //eslint-disable-next-line
  }, []);

useEffect(() => {
    if (props.employee.closeModal) {
      setShowModal(false);
    }
  },[props.employee.closeModal]);


  const handleModal = (name) => {
    setSelectModal(name);
    setShowModal(!showModal);
  };

  const handleTableChange = (pagination, filters, sorter) => {
    const pager = { ...pagination };
    pager.current = pagination.current;
    setPagination(pager);
    fetch({
      results: pagination.pageSize,
      page: pagination.current,
      sortField: sorter.field,
      sortOrder: sorter.order,
      ...filters,
    });
  };

  const fetch = (params = {}) => {
    setLoading(true);
    if (Object.keys(params).length) {
      setSearchParams(params);
    }
  };

  useEffect(() => {
    Axios.get("https://randomuser.me/api", {
      params: {
        results: 10,
        ...searchParams,
        searchInput: searchInput,
      },
    }).then((res) => {
      const paginationData = { ...pagination };
      paginationData.total = 200;
      setData(res.data.results);
      setPagination(paginationData);
      setLoading(false);
    });
  }, [searchParams]);

  const columns = [
    {
      title: "Emp Id",
      dataIndex: "dob",
      sorter: true,
      render: (dob) => dob.age,
    },
    {
      title: "Name",
      dataIndex: "name",
      sorter: true,
      render: (name) => `${name.first} ${name.last}`,
    },
    {
      title: "Email",
      dataIndex: "email",
    },
    {
      title: "Address",
      dataIndex: "location",
      render: (location) => location.city,
      filters: [
        { text: "AAG", value: "AAG" },
        { text: "Vantage 9", value: "Vantage 9" },
        { text: "Adisa", value: "Adisa" },
      ],
    },
    {
      title: "Unit",
      dataIndex: "gender",
      filters: [
        { text: "Male", value: "male" },
        { text: "Female", value: "female" },
      ],
    },
    {
      title: "Status",
      dataIndex: "nat",
      filters: [
        { text: "Onboarding", value: "DE" },
        { text: "Offboarding", value: "ES" },
        { text: "Inactive", value: "IR" },
      ],
    },
    {
      title: "Action",
      render: () => (
        <div>
          <Button type="primary" size="small" className="m-1">
            Show
          </Button>
          <Button size="small" className="m-1" onClick={() => handleModal("edit")}>
            Edit
          </Button>
        </div>
      ),
    },
  ];
  const selectBefore = (
    <Select defaultValue="name" className="select-before">
      <Option value="emp_id">Emp ID</Option>
      <Option value="name">Name</Option>
      <Option value="email">Email</Option>
    </Select>
  );

  return (
    <div className="my-3">
      <div className="my-3 d-flex flex-column justify-content-between">
        <div className="w-50 w-sm-50">
          <Search
            addonBefore={selectBefore}
            placeholder="input search text"
            enterButton="Search"
            size="large"
            onChange={(e) => setSearchInput(e.target.value)}
            onSearch={(value) => setSearchParams({ ...searchParams, searchInput })}
          />
        </div>
        <Button.Group className="my-sm-3">
          <Button
            type="primary"
            style={{ margin: "0 2px" }}
            shape="round"
            size="large"
            onClick={() => handleModal("add")}
          >
            {/* <Icon type="plus" /> */}
            Add Employee
          </Button>
          <Button
            type="primary"
            style={{ margin: "0 2px" }}
            shape="round"
            size="large"
            onClick={() => handleModal("upload")}
          >
            {/* <Icon type="upload" /> */}
            Add Employee List
          </Button>
        </Button.Group>
      </div>
      <Table
        bordered
        columns={columns}
        rowKey={(record) => record.login.uuid}
        dataSource={data}
        pagination={pagination}
        loading={loading}
        onChange={handleTableChange}
        className="overflow-auto"
      />
      <Modal visible={showModal} onCancel={handleModal}>
        {selectModal === "upload" && <BulkUpload />}
        {selectModal === "add" && <AddEmployee />}
        {selectModal === "edit" && <EditEmployee />}
      </Modal>
    </div>
  );
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  messages: state.messages,
  employee:state.employee
});

export default connect(mapStateToProps, { })(Dashboard);
