import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import Button from "@material-ui/core/Button";
import ListItemText from "@material-ui/core/ListItemText";

import { getEmployeeList } from "../../../actions/employee/employeeActions";

const Dashboard = (props) => {
  const [data, setData] = useState([]);
  useEffect(() => {
    if (Object.keys(props.employee.employeeList).length > 0) {
      console.log(">>>Data>>", props.employee.employeeList);
      setData(newData);
    }
  }, [props.employee.employeeList]);

  // const listItems = props.employee.employeeList.map((id) => <li>{id}</li>);

  const getData = () => {
    props.getEmployeeList();
  };
  return (
    <div className="my-3">
      <h3>Dashboard</h3>
      <Button variant="contained" color="primary" onClick={getData}>
        Get Data
      </Button>
      <List component="nav" aria-label="main mailbox folders">
        <ListItem>
          {props.employee.employeeList.map((id) => {
            return <ListItemText primary={id} />;
          })}
        </ListItem>
      </List>
    </div>
  );
};

const mapStateToProps = (state) => ({
  employee: state.employee,
  messages: state.messages,
});

export default connect(mapStateToProps, { getEmployeeList })(Dashboard);
