import React, { useEffect, useState } from "react";
import { Form, Card, Button, Tabs, DatePicker, Input, Select } from "antd";
import {
  editEmployee,
  getEmployeeDetails,
} from "../../../actions/employee/employeeActions";

import { connect } from "react-redux";
import { Row, Col } from "antd";
import moment from "moment";
import { Spin } from "antd";
const { TabPane } = Tabs;
const { Option } = Select;

const EditEmployee = (props) => {
  const [empData, setEmpData] = useState({});
  const [locationArray, setLocationArry] = useState(props.location);
  const [projectArray, setProjectArry] = useState(props.projects);
  const [loading, setLoading] = useState(true);
  const [spin, setSpin] = useState(false);
  const [editParams, setEditParams] = useState({});

  useEffect(() => {
    props.getEmployeeDetails(props.empid);
  }, []);

  useEffect(() => {
    if (Object.keys(props.employee.employeeDetails).length > 0) {
      if (props.employee.employeeDetails.EMPID === props.empid) {
        setEmpData(props.employee.employeeDetails);
        setLoading(false);
      }
    }
  }, [props.employee.employeeDetails]);

  useEffect(() => {
    if (props.messages.error.length > 0) {
      setSpin(false);
    }
  }, [props.messages.error]);

  // useEffect(() => {
  //    setLoading(true);
  // }, [props.employee.loading])

  const layout = {
    labelCol: {
      span: 4,
    },
    wrapperCol: {
      span: 24,
    },
  };
  const onFinish = (values) => {
    const newEmp = {
      ...editParams,
      DOB: editParams.DOB && moment(editParams.DOB).format("YYYY-MM-DD"),
      DOJ: editParams.DOJ && moment(editParams.DOJ).format("YYYY-MM-DD"),
      LASTWORKINGDATE: editParams.LASTWORKINGDATE &&
     (moment(editParams.LASTWORKINGDATE).format("YYYY-MM-DD") === "Invalid date"
          ? null
          : moment(editParams.LASTWORKINGDATE).format("YYYY-MM-DD")),
      ID: props.empid,
    };
    console.log(newEmp);
    // setSpin(true);
    // props.editEmployee(newEmp);
  };

  const onFinishFailed = (errorInfo) => {};
  const [form] = Form.useForm();

  return (
    <div>
      <h2>Edit Employee Details</h2>
      <Spin spinning={spin} size="large">
        <Card
          title={empData.FIRSTNAME && empData.FIRSTNAME + " " + empData.LASTNAME}
          bordered={true}
          className="my-3"
          loading={loading}
        >
          <Form
            {...layout}
            size="middle"
            name="basic"
            form={form}
            onFinish={onFinish}
            onFinishFailed={onFinishFailed}
            initialValues={{
              FIRSTNAME: empData.FIRSTNAME,
              MIDDLENAME: empData.MIDDLENAME,
              LASTNAME: empData.LASTNAME,
              GENDER: empData.GENDER,
              EMAIL: empData.EMAIL,
              MOBILENUMBER: empData.MOBILENUMBER,
              CAREERTRACK: empData.CAREERTRACK,
              EMPTYPE: empData.EMPTYPE,
              DOB: moment(empData.DOB),
              DOJ: moment(empData.DOJ),
              DESIGNATION: empData.DESIGNATION,
              STATUS: empData.STATUS,
              UNIT: empData.UNIT,
              DEPARTMENT: empData.DEPARTMENT,
              DEPARTMENT: empData.DEPARTMENT,
              MANAGER: empData.MANAGER,
              SEATID: empData.SEATID,
              CURRENTLOCATION: empData.CURRENTLOCATION && empData.CURRENTLOCATION.ID,
              BASELOCATION: empData.BASELOCATION && empData.BASELOCATION.ID,
              PROJECTS: empData.PROJECTS && empData.PROJECTS.ID,
              EXTENSIONNUMBER: empData.EXTENSIONNUMBER,
              LASTWORKINGDATE:
                empData.LASTWORKINGDATE !== null
                  ? moment(empData.LASTWORKINGDATE)
                  : null,
            }}
          >
            <Tabs defaultActiveKey="1">
              <TabPane tab="Personal Details" key="1">
                <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                  <Col className="gutter-row" span={12}>
                    <label>First Name </label>
                    <Form.Item
                      name="FIRSTNAME"
                      hasFeedback
                      rules={[
                        { required: true, message: "Please input Firstname!" },
                      ]}
                    >
                      <Input
                        placeholder="firstname"
                        onChange={(e) => setEditParams({...editParams, FIRSTNAME:e.target.value})}
                      />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Middle Name </label>
                    <Form.Item name="MIDDLENAME">
                      <Input placeholder="middlename" onChange={(e) => setEditParams({...editParams, MIDDLENAME:e.target.value})} />
                    </Form.Item>
                  </Col>

                  <Col className="gutter-row" span={12}>
                    <label>Last Name </label>
                    <Form.Item
                      name="LASTNAME"
                      hasFeedback
                      rules={[{ required: true, message: "Please input Lastname!" }]}
                    >
                      <Input placeholder="lastname" onChange={(e) => setEditParams({...editParams, LASTNAME:e.target.value})} />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Gender</label>
                    <Form.Item
                      name="GENDER"
                      hasFeedback
                      rules={[
                        {
                          required: true,
                          message: "Please select Gender!",
                        },
                      ]}
                    >
                      <Select placeholder="Select Gender" onChange={(value) => setEditParams({...editParams, GENDER:value})} >
                        <Option value="male">Male</Option>
                        <Option value="female">Female</Option>
                      </Select>
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Email</label>
                    <Form.Item
                      name="EMAIL"
                      rules={[{ type: 'email', message: "Email is not Valid!" }]}
                    >
                      <Input disabled onChange={(e) => setEditParams({...editParams, EMAIL:e.target.value})} />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Mobile</label>
                    <Form.Item
                      name="MOBILENUMBER"
                      hasFeedback
                      rules={[
                        { required: true, message: "Please enter Mobile Number!" },
                        { min: 10, message: "Minimum length should be 10" },
                      ]}
                    >
                      <Input type="number" autoComplete="off" onChange={(e) => setEditParams({...editParams, MOBILENUMBER:e.target.value})} />
                    </Form.Item>
                  </Col>

                  <Col className="gutter-row" span={12}>
                    <label>DOB</label>
                    <Form.Item
                      name="DOB"
                      rules={[
                        {
                          type: "object",
                          required: true,
                          message: "Please input your DOB",
                        },
                      ]}
                    >
                      <DatePicker onChange={(date, dateString) => setEditParams({...editParams, DOB:date})} />
                    </Form.Item>
                  </Col>
                </Row>
              </TabPane>

              <TabPane tab="Business Details" key="2">
                <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                  <Col className="gutter-row" span={12}>
                    <label>Careertrack</label>
                    <Form.Item name="CAREERTRACK">
                      <Input placeholder="careertrack" onChange={(e) => setEditParams({...editParams, CAREERTRACK:e.target.value})} />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Departname </label>
                    <Form.Item name="DEPARTMENT">
                      <Input placeholder="Departname" onChange={(e) => setEditParams({...editParams, DEPARTMENT:e.target.value})} />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Manager </label>
                    <Form.Item name="MANAGER">
                      <Input placeholder="Manager" onChange={(e) => setEditParams({...editParams, MANAGER:e.target.value})} />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>EMP Type</label>
                    <Form.Item
                      name="EMPTYPE"
                      hasFeedback
                      rules={[
                        {
                          required: true,
                          message: "Please select Employee Type!",
                        },
                      ]}
                    >
                      <Select placeholder="Select Employee Type" onChange={(value) => setEditParams({...editParams, EMPTYPE:value})} >
                        <Option value="regular">Regular</Option>
                        <Option value="contractor">Contractor</Option>
                        <Option value="intern">Intern</Option>
                      </Select>
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Designation </label>
                    <Form.Item
                      name="DESIGNATION"
                      hasFeedback
                      rules={[
                        {
                          required: true,
                          message: "Please select Designation!",
                        },
                      ]}
                    >
                      <Select placeholder="Select Designation" onChange={(value) => setEditParams({...editParams, DESIGNATION:value})} >
                        <Option value="engineer">Engineer</Option>
                        <Option value="lead">Lead</Option>
                        <Option value="manager">Manager</Option>
                      </Select>
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Status </label>
                    <Form.Item
                      name="STATUS"
                      hasFeedback
                      rules={[
                        {
                          required: true,
                          message: "Please select Status!",
                        },
                      ]}
                    >
                      <Select placeholder="Select Status" onChange={(value) => setEditParams({...editParams, STATUS:value})} >
                        <Option value="onboarding">Onboarding</Option>
                        <Option value="offboarding">Offboarding</Option>
                        <Option value="inactive">Inactive</Option>
                      </Select>
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>UNIT </label>
                    <Form.Item name="UNIT">
                      <Select placeholder="Select Unit" onChange={(value) => setEditParams({...editParams, UNIT:value})} >
                        <Option value="CIS">CIS</Option>
                        <Option value="IDM">IDM</Option>
                        <Option value="Other">Other</Option>
                      </Select>
                    </Form.Item>
                  </Col>

                  <Col className="gutter-row" span={12}>
                    <label>Project </label>
                    <Form.Item name="PROJECTS">
                      <Select placeholder="Select project"  onChange={(value) => setEditParams({...editParams, PROJECTS:value})} >
                        {projectArray &&
                          projectArray.map((data) => (
                            <Option value={data.ID} key={data.ID}>
                              {data.NAME ? data.NAME : ""}
                            </Option>
                          ))}
                      </Select>
                    </Form.Item>
                  </Col>

                  <Col className="gutter-row" span={12}>
                    <label>Base Location </label>
                    <Form.Item name="BASELOCATION">
                      <Select placeholder="Select Base Location"  onChange={(value) => setEditParams({...editParams, BASELOCATION:value})} >
                        {locationArray.map((data) => (
                          <Option value={data.value} key={data.value}>
                            {data.text}
                          </Option>
                        ))}
                      </Select>
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Current Location </label>
                    <Form.Item name="CURRENTLOCATION">
                      <Select placeholder="Select Current Location"  onChange={(value) => setEditParams({...editParams, CURRENTLOCATION:value})} >
                        {locationArray.map((data) => (
                          <Option value={data.value} key={data.value}>
                            {data.text}
                          </Option>
                        ))}
                      </Select>
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Seat No </label>
                    <Form.Item name="SEATID">
                      <Input placeholder="Seat No" onChange={(e) => setEditParams({...editParams, SEATID:e.target.value})} />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Extension No </label>
                    <Form.Item name="EXTENSIONNUMBER">
                      <Input placeholder="Extension No" onChange={(e) => setEditParams({...editParams, EXTENSIONNUMBER:e.target.value})} />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>DOJ</label>
                    <Form.Item
                      name="DOJ"
                      rules={[
                        {
                          type: "object",
                          required: true,
                          message: "Please input your DOJ",
                        },
                      ]}
                    >
                      <DatePicker  onChange={(date, dateString) => setEditParams({...editParams, DOJ:date})} />
                    </Form.Item>
                  </Col>
                  <Col className="gutter-row" span={12}>
                    <label>Last Working Date</label>
                    <Form.Item 
                        name="LASTWORKINGDATE"
                        rules={[
                          {
                            type: "object",
                            required: true,
                            message: "Please input your LWD",
                          },
                        ]}
                      >
                                              <DatePicker  onChange={(date, dateString) => setEditParams({...editParams, LASTWORKINGDATE:date})} />
                    </Form.Item>
                  </Col>
                </Row>
              </TabPane>
            </Tabs>
            <Row justify="end">
              <Form.Item className="m-1">
                <Button type="primary" htmlType="submit">
                  Update
                </Button>
              </Form.Item>
            </Row>
          </Form>
        </Card>
      </Spin>
    </div>
  );
};
const mapStateToProps = (state) => ({
  messages: state.messages,
  employee: state.employee,
});
export default connect(mapStateToProps, { editEmployee, getEmployeeDetails })(
  EditEmployee
);
