import React, { useState, useEffect } from "react";
import { Form, Upload, Card, Button, Table } from "antd";
import { InboxOutlined } from "@ant-design/icons";
import { connect } from "react-redux";
import { addEmployeeList } from "../../../actions/employee/employeeActions";
import PropTypes from "prop-types";
import sample1 from "../../../constant/Data.xlsx";
import sample2 from "../../../constant/Data.csv";

const BulkUpload = (props) => {
  const [file, setFile] = useState(null);
  const [errors, setErrors] = useState([]);

  useEffect(() => {
    setErrors(props.employee.errors);
  }, [props.employee.errors]);

  useEffect(() => {
     if (!props.employee.loading) {
        setErrors([]);
     }
  }, []);

  const uploadFile = (file) => {
    setFile(file);
  };

  const onFinish = (values) => {
    const formData = new FormData();
    formData.append("file", file);
    const empData = {
      filename: file.name,
      formData: formData,
    };
    props.addEmployeeList(empData);
  };
  const columns = [
    {
      title: "Row No",
      dataIndex: "no",
    },
    {
      title: "Field",
      dataIndex: "row",
    },
    {
      title: "Message",
      dataIndex: "msg",
    },
  ];
  const uploadprops = {
   onRemove: file => {
      setFile(null);
   },
   beforeUpload: file => {
      setFile(file);
     return false;
   },
   file,
   multiple: false
 };
  return (
    <div>
      <Card title="Upload Employee List" bordered={true} className="my-3">
        <Form layout="vertical" onFinish={onFinish}>
          <Form.Item
            label="Select file to upload"
            rules={[{ required: true, message: "File is required" }]}
          >
            <Upload.Dragger {...uploadprops}>
              <p className="ant-upload-drag-icon">
                <InboxOutlined />
              </p>
              <p className="ant-upload-text">
                Click or drag file to this area to upload
              </p>
              <p className="ant-upload-hint">Support for a single or bulk upload.</p>
            </Upload.Dragger>
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit" block>
              Upload
            </Button>
            <div className="m-1">
              <a href={sample1} download="sample_xlsx">
                Download sample xlsx
              </a>
              /
              <a href={sample2} download="sample.csv">
                Download sample csv
              </a>
            </div>
          </Form.Item>
        </Form>
        {errors.length > 0 && (
          <Table
            columns={columns}
            dataSource={errors}
            rowKey={(no) => no}
            size="small"
            bordered
            pagination={false}
            className="error-table"
          />
        )}
      </Card>
    </div>
  );
};
BulkUpload.propTypes = {
  addEmployeeList: PropTypes.func.isRequired,
  employee: PropTypes.object
};
const mapStateToProps = (state) => ({
  employee: state.employee,
});
export default connect(mapStateToProps, { addEmployeeList })(BulkUpload);
