import {
  GET_EMPLOYEES_LIST,
  GET_SINGLE_EMPLOYEE,
  ADD_EMPLOYEE,
  UPLOAD_EMPLOYEES_LIST,
  UPLOAD_EMPLOYEES_LIST_SUCCESS,
  UPLOAD_EMPLOYEES_LIST_FAIL,
} from "./employeeTypes";

export const getEmployeeList = () => {
  return {
    type: GET_EMPLOYEES_LIST,
  };
};

export const getEmployeeDetails = (eid) => {
  return {
    type: GET_SINGLE_EMPLOYEE,
    payload: eid,
  };
};

export const addEmployee = (employee) => {
  return {
    type: ADD_EMPLOYEE,
    payload: employee,
  };
};

export const uploadEmployeeList = (employeeList) => {
  return {
    type: UPLOAD_EMPLOYEES_LIST,
    employeeList,
  };
};

export const uploadEmployeeListSuccess = (employeeList) => ({
  type: UPLOAD_EMPLOYEES_LIST_SUCCESS,
  employeeList,
});

export const uploadEmployeeListFail = (error) => ({
  type: UPLOAD_EMPLOYEES_LIST_FAIL,
  error,
});
