import React, { useState, useEffect } from "react";
import { Table, Input, Button, Select, Modal, message } from "antd";
import { connect } from "react-redux";

import { getEmployeeList } from "../../../actions/employee/employeeActions";

const Dashboard = (props) => {


  useEffect(() => {
    if (Object.keys(props.employee.employeeList).length > 0) {
      const newData = props.employee.employeeList.results.map((emp) => {
        return {
          ...emp,
          NAME: {
            FIRSTNAME: emp.FIRSTNAME,
            LASTNAME: emp.LASTNAME,
          },
        };
      });
      const paginationData = { ...pagination };
      paginationData.total = props.employee.employeeList.count;
      setData(newData);
      setPagination(paginationData);
      setLoading(false);
    }
  }, [props.employee.employeeList]);

  const getData = () => {
    props.getEmployeeList();
  };
  return (
    <div className="my-3">
      <h3>Dashboard</h3>
      <Button variant="contained" color="primary" onClick={getData}>
        Get Data
      </Button>
    </div>
  );
};

const mapStateToProps = (state) => ({
  employee: state.employee,
  messages: state.messages,
});

export default connect(mapStateToProps, { getEmployeeList })(Dashboard);
