import {
  GET_EMPLOYEES_LIST_SUCCESS,
  GET_SINGLE_EMPLOYEE_SUCCESS,
  ADD_EMPLOYEE_FAIL,
  ADD_EMPLOYEE_SUCCESS,
  SET_LOADING,
  GET_EMPLOYEES_LIST_FAIL,
  GET_SINGLE_EMPLOYEE_FAIL,
  UPLOAD_EMPLOYEES_LIST,
  UPLOAD_EMPLOYEES_LIST_SUCCESS,
  UPLOAD_EMPLOYEES_LIST_FAIL,
} from "../../actions/employee/employeeTypes";

const initialState = {
  employeeList: [],
  employeeDetails: {},
  errors: [],
  loading: false,
  closeModal: false,
};

export default function (state = initialState, action) {
  switch (action.type) {
    case GET_EMPLOYEES_LIST_SUCCESS:
      return {
        ...state,
        employeeList: action.payload,
      };

    case GET_SINGLE_EMPLOYEE_SUCCESS:
      return {
        ...state,
        employeeDetails: action.payload,
      };

    case SET_LOADING:
      return {
        ...state,
        loading: !state.loading,
      };
    case ADD_EMPLOYEE_SUCCESS:
      return {
        ...state,
        closeModal: true,
        employeeList: [...state.employeeList, action.payload],
      };
    case ADD_EMPLOYEE_FAIL:
      return {
        ...state,
        errors: [...state.errors, action.payload],
      };
    case GET_EMPLOYEES_LIST_FAIL:
    case GET_SINGLE_EMPLOYEE_FAIL:
      return {
        ...state,
        errors: [...state.errors, action.payload],
      };
    case UPLOAD_EMPLOYEES_LIST:
      console.log("action", action);
      return { ...state };
    case UPLOAD_EMPLOYEES_LIST_SUCCESS:
      console.log("action success", action);
      return {
        ...state,
        isLoading: "",
      };
    case UPLOAD_EMPLOYEES_LIST_FAIL:
      return { ...state, error: action.error, isLoading: "" };
    default:
      return state;
  }
}
