import React, { useState, useEffect } from "react";
import { Table, Input, Button, Select, Modal } from "antd";
import { connect } from "react-redux";
// import { Icon } from 'antd';
// import { useAlert } from "react-alert";
import Axios from "axios";
import BulkUpload from "../bulkupload/BulkUpload";
import AddEmployee from "../addemployee/AddEmployee";
import EditEmployee from "../editemployee/EditEmployee";
import ShowEmployee from "../showemployee/ShowEmployee";
const { Option } = Select;
const { Search } = Input;


const Dashboard = (props) => {
  const paginationConfig = {
    defaultPageSize: 5,
    showSizeChanger: true,
    pageSizeOptions: ["5", "10", "20", "50", "100"],
  };
  const [data, setData] = useState([]);
  const [pagination, setPagination] = useState(paginationConfig);
  const [loading, setLoading] = useState(false);
  const [searchInput, setSearchInput] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [selectModal, setSelectModal] = useState("");
  const [searchParams, setSearchParams] = useState({});
  const [showEmp, setShowEmp] = useState("");
  const [editEmp, setEditEmp] = useState("");
  const [projects, setProjects] = useState(null);
  const [location, setLocation] = useState(null);
  // const alert = useAlert();
  useEffect(() => {
    fetch();
    //eslint-disable-next-line
  }, []);

  useEffect(() => {
    if (props.employee.closeModal) {
      setShowModal(false);
      // alert.success("You have successfully Register !");
    }
  }, [props.employee.closeModal]);

  useEffect(() => {
    Axios.get('/locations').then((res) => {
      setLocation(res.data);
    });
    projects
  }, []);

  const handleModal = (name, id) => {
    setEditEmp(id);
    setSelectModal(name);
    setShowModal(!showModal);
  };
  const showEmpModal = (name, id) => {
    setShowEmp(id);
    setSelectModal(name);
    setShowModal(!showModal);
  };

  const handleTableChange = (pagination, filters, sorter) => {
    const pager = { ...pagination };
    pager.current = pagination.current;
    setPagination(pager);
    fetch({
      results: pagination.pageSize,
      page: pagination.current,
      sortField: sorter.field,
      sortOrder: sorter.order,
      ...filters,
    });
  };

  const fetch = (params = {}) => {
    setLoading(true);
    if (Object.keys(params).length) {
      setSearchParams({
        ...params,
        offset: params.results * (params.page - 1),
        limit: params.results,
      });
    }
  };

  useEffect(() => {
    Axios.get("/employees/", {
      params: {
        limit: 10,
        ...searchParams,
        searchInput: searchInput,
      },
    }).then((res) => {
      const paginationData = { ...pagination };
      paginationData.total = res.data.count;
      setData(res.data.results);
      setPagination(paginationData);
      setLoading(false);
    });
    //eslint-disable-next-line
  }, [searchParams]);

  const columns = [
    {
      title: "Emp Id",
      dataIndex: "EMPID",
      sorter: true,
    },
    {
      title: "Name",
      dataIndex: "FIRSTNAME",
      sorter: true,
    },
    {
      title: "Email",
      dataIndex: "EMAIL",
    },
    {
      title: "Address",
      dataIndex: "CURRENTLOCATION",
      render: (CURRENTLOCATION) => CURRENTLOCATION.ADDRESS,
      filters: [
        { text: "AAG", value: "AAG" },
        { text: "Vantage 9", value: "Vantage 9" },
        { text: "Adisa", value: "Adisa" },
      ],
    },
    {
      title: "Unit",
      dataIndex: "UNIT",
      filters: [
        { text: "Male", value: "male" },
        { text: "Female", value: "female" },
      ],
    },
    {
      title: "Status",
      dataIndex: "STATUS",
      filters: [
        { text: "Onboarding", value: "DE" },
        { text: "Offboarding", value: "ES" },
        { text: "Inactive", value: "IR" },
      ],
    },
    {
      title: "Action",
      dataIndex: "EMPID",
      render: (EMPID) => (
        <div>
          <Button
            type="primary"
            size="small"
            className="m-1"
            onClick={() => showEmpModal("show", EMPID)}
          >
            Show
          </Button>
          <Button
            type="primary"
            size="small"
            className="m-1"
            onClick={() => handleModal("edit", EMPID)}
          >
            Edit
          </Button>
        </div>
      ),
    },
  ];
  const selectBefore = (
    <Select defaultValue="name" className="select-before">
      <Option value="emp_id">Emp ID</Option>
      <Option value="name">Name</Option>
      <Option value="email">Email</Option>
    </Select>
  );

  return (
    <div className="my-3">
      <h3>Employees</h3>
      <div className="my-3 d-flex flex-column justify-content-between">
        <div className="w-50 w-sm-50">
          <Search
            addonBefore={selectBefore}
            placeholder="input search text"
            enterButton="Search"
            size="large"
            onChange={(e) => setSearchInput(e.target.value)}
            onSearch={(value) => setSearchParams({ ...searchParams, searchInput })}
          />
        </div>
        <Button.Group className="my-sm-3">
          <Button
            type="primary"
            style={{ margin: "0 2px" }}
            shape="round"
            size="large"
            onClick={() => handleModal("add")}
          >
            {/* <Icon type="plus" /> */}
            Add Employee
          </Button>
          <Button
            type="primary"
            style={{ margin: "0 2px" }}
            shape="round"
            size="large"
            onClick={() => handleModal("upload")}
          >
            {/* <Icon type="upload" /> */}
            Add Employee List
          </Button>
        </Button.Group>
      </div>
      <Table
        bordered
        columns={columns}
        rowKey={(ID) => ID}
        dataSource={data}
        pagination={pagination}
        loading={loading}
        onChange={handleTableChange}
        className="overflow-auto"
      />
      <Modal
        visible={showModal}
        onCancel={handleModal}
        footer={
          [
            // <Button key="back" onClick={handleModal}>
            //   Return
            // </Button>,
            // <Button key="submit" type="primary" loading={loading} onClick={handleModal}>
            //   Submit
            // </Button>,
          ]
        }
      >
        {selectModal === "upload" && <BulkUpload />}
        {selectModal === "add" && <AddEmployee location={location} />}
        {selectModal === "edit" && location !== null && <EditEmployee empid={editEmp} location={location}  />}
        {selectModal === "show" && <ShowEmployee empid={showEmp} />}
      </Modal>
    </div>
  );
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  messages: state.messages,
  employee: state.employee,
});

export default connect(mapStateToProps, {})(Dashboard);
