import React, { useEffect, useState } from "react";
import Axios from "axios";
import { Card, Descriptions, Col, Row } from "antd";
import { getEmployeeDetails } from "../../../actions/employee/employeeActions";
import { connect } from "react-redux";

const ShowEmployee = (props) => {
  const [empData, setEmpData] = useState({});
  const [loading, setLoading] = useState(true);
   useEffect(() => {
      props.getEmployeeDetails(props.empid);
      Axios.get(`/employees/${props.empid}`).then((res) => {
         setEmpData(res.data);
         setLoading(false);
      });
   }, []);

  return (
    <div>
      <Card title={empData.FIRSTNAME && empData.FIRSTNAME + ' ' + empData.LASTNAME} bordered={true} className="my-3" loading={loading}>
         <Descriptions bordered size="small"  column={1}>
            <Descriptions.Item label="Emp Id:">{empData.EMPID}</Descriptions.Item>
            <Descriptions.Item label="Email:">{empData.EMAIL}</Descriptions.Item>
            <Descriptions.Item label="Address:">{empData.CURRENTLOCATION && empData.CURRENTLOCATION.ADDRESS}</Descriptions.Item>
            <Descriptions.Item label="Unit:">{empData.UNIT}</Descriptions.Item>
            <Descriptions.Item label="Status:">{empData.STATUS}</Descriptions.Item>
            <Descriptions.Item label="Date of Joining:">{empData.DOJ}</Descriptions.Item>
            <Descriptions.Item label="Current Project:">{empData.PROJECTS && empData.PROJECTS.NAME}</Descriptions.Item>
         </Descriptions>
      </Card>
    </div>
  );
};
const mapStateToProps = (state) => ({
   employee: state.employee
});

export default connect(mapStateToProps, {getEmployeeDetails})(ShowEmployee);
