import React from "react";
import { Form, Card, Button, Tabs, DatePicker, Input, Select } from "antd";
import { addEmployee } from "../../../actions/employee/employeeActions";
import { connect } from "react-redux";
import moment from "moment";
const { TabPane } = Tabs;
const { Option } = Select;

const AddEmployee = (props) => {
  const layout = {
    labelCol: {
      span: 8,
    },
    wrapperCol: {
      span: 16,
    },
  };
  const onFinish = (values) => {
    console.log("Success:", values);
    const newEmp = {
      ...values,
      DOB: moment(values.DOB).format("YYYY-MM-DD"),
      DOJ: moment(values.DOJ).format("YYYY-MM-DD"),
    };
    console.log(newEmp);
    props.addEmployee(newEmp);
  };

  const onFinishFailed = (errorInfo) => {
    console.log("Failed:", errorInfo);
  };
  const [form] = Form.useForm();
  return (
    <div>
      <Card title="Add New Employee" bordered={true} className="my-3">
        <Form
          {...layout}
          name="basic"
          form={form}
          onFinish={onFinish}
          onFinishFailed={onFinishFailed}
        >
          <Tabs defaultActiveKey="1">
            <TabPane tab="Personal Details" key="1">
              <Form.Item
                name="FIRSTNAME"
                label="Firstname"
                hasFeedback
                rules={[{ required: true, message: "Please input Firstname!" }]}
              >
                <Input placeholder="firstname" />
              </Form.Item>
              <Form.Item
                name="MIDDLENAME"
                label="Middlename"
                hasFeedback
                rules={[{ required: true, message: "Please input Middlename!" }]}
              >
                <Input placeholder="middlename" />
              </Form.Item>
              <Form.Item
                name="LASTNAME"
                label="Lastname"
                hasFeedback
                rules={[{ required: true, message: "Please input Lastname!" }]}
              >
                <Input placeholder="lastname" />
              </Form.Item>
              <Form.Item
                name="GENDER"
                label="Gender"
                hasFeedback
                rules={[
                  {
                    required: true,
                    message: "Please select Gender!",
                  },
                ]}
              >
                <Select placeholder="Select Gender">
                  <Option value="male">Male</Option>
                  <Option value="female">Female</Option>
                </Select>
              </Form.Item>
              <Form.Item
                name="DOB"
                label="Date of Birth"
                rules={[
                  {
                    type: "object",
                    required: true,
                    message: "Please input your DOB",
                  },
                ]}
              >
                <DatePicker />
              </Form.Item>
              <Form.Item
                name="CAREERTRACK"
                label="Careertrack"
                hasFeedback
                rules={[{ required: true, message: "Please enter Careertrack!" }]}
              >
                <Input placeholder="careertrack" />
              </Form.Item>
              <Form.Item
                name="MOBILENUMBER"
                label="Mobile No."
                hasFeedback
                rules={[
                  { required: true, message: "Please enter Mobile Number!" },
                  { min: 10, message: "Minimum length should be 10" },
                ]}
              >
                <Input type="number" autoComplete="off" />
              </Form.Item>
              <Form.Item
                name="EMPTYPE"
                label="Employee Type"
                hasFeedback
                rules={[
                  {
                    required: true,
                    message: "Please select Employee Type!",
                  },
                ]}
              >
                <Select placeholder="Select Employee Type">
                  <Option value="regular">Regular</Option>
                  <Option value="contractor">Contractor</Option>
                  <Option value="intern">Intern</Option>
                </Select>
              </Form.Item>
              <Form.Item
                name="DOJ"
                label="Date of Joining"
                rules={[
                  {
                    type: "object",
                    required: true,
                    message: "Please input your DOJ",
                  },
                ]}
              >
                <DatePicker />
              </Form.Item>
            </TabPane>
            <TabPane tab="Business Details" key="2">
              <Form.Item
                name="DESIGNATION"
                label="Designation"
                hasFeedback
                rules={[
                  {
                    required: true,
                    message: "Please select Designation!",
                  },
                ]}
              >
                <Select placeholder="Select Designation">
                  <Option value="engineer">Engineer</Option>
                  <Option value="lead">Lead</Option>
                  <Option value="manager">Manager</Option>
                </Select>
              </Form.Item>
              <Form.Item
                name="STATUS"
                label="Status"
                hasFeedback
                rules={[
                  {
                    required: true,
                    message: "Please select Status!",
                  },
                ]}
              >
                <Select placeholder="Select Status">
                  <Option value="onboarding">Onboarding</Option>
                  <Option value="offboarding">Offboarding</Option>
                  <Option value="inactive">Inactive</Option>
                </Select>
              </Form.Item>
              <Form.Item
                name="UNIT"
                label="Unit"
                hasFeedback
                rules={[
                  {
                    required: true,
                    message: "Please select Unit!",
                  },
                ]}
              >
                <Select placeholder="Select Unit">
                  <Option value="cis">CIS</Option>
                  <Option value="idm">IDM</Option>
                  <Option value="other">Other</Option>
                </Select>
              </Form.Item>
              <Form.Item
                name="DEPARTMENT"
                label="Departname"
                hasFeedback
                rules={[{ required: true, message: "Please input Departname!" }]}
              >
                <Input placeholder="Departname" />
              </Form.Item>
              <Form.Item
                name="PROJECTS"
                label="Project"
                hasFeedback
                rules={[
