import React, { useEffect, useState } from "react";
import Axios from "axios";
import { Form, Card, Button, Tabs, DatePicker, Input, Select } from "antd";
import { getEmployeeDetails } from "../../../actions/employee/employeeActions";
import { connect } from "react-redux";
import { Row, Col } from "antd";
import moment from "moment";
const { TabPane } = Tabs;
const { Option } = Select;

const EditEmployee = (props) => {
  const [empData, setEmpData] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    props.getEmployeeDetails(props.empid);
    Axios.get(`/employees/${props.empid}`).then((res) => {
      setEmpData(res.data);
      setLoading(false);
    });
  }, []);

  const layout = {
    labelCol: {
      span: 4,
    },
    wrapperCol: {
      span: 24,
    },
  };
  const onFinish = (values) => {
    const newEmp = {
      ...values,
      DOB: moment(values.DOB).format("YYYY-MM-DD"),
      DOJ: moment(values.DOJ).format("YYYY-MM-DD"),
    };
    console.log("Success:", newEmp);
  };

  const onFinishFailed = (errorInfo) => {
    console.log(">>>>>Failed>>>>:", errorInfo);
  };
  const [form] = Form.useForm();
  const CustomizedForm: React.FC<CustomizedFormProps> = ({ onChange, fields }) => {
  return (
    <div>
      <h2>Edit Employee Details : {props.empid}</h2>
      <Card
        title={empData.FIRSTNAME && empData.FIRSTNAME + " " + empData.LASTNAME}
        bordered={true}
        className="my-3"
        loading={loading}
      >
        <Form
          {...layout}
          size="middle"
          name="basic"
          form={form}
          onFinish={onFinish}
          onFinishFailed={onFinishFailed}
          fields={fields}
          onFieldsChange={(changedFields, allFields) => {
            onChange(allFields);
          }}
        >
          <Tabs defaultActiveKey="1">
            <TabPane tab="Personal Details" key="1">
              <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                <Col className="gutter-row" span={12}>
                  <label>First Name </label>
                  <Form.Item
                    name="FIRSTNAME"
                    hasFeedback
                    rules={[{ required: true, message: "Please input Firstname!"  }]}
                  >
                    <Input
                      placeholder="firstname"
                      defaultValue={empData.FIRSTNAME}
                    />
                  </Form.Item>
                </Col>
                <Col className="gutter-row" span={12}>
                  <label>Middle Name </label>
                  <Form.Item
                    name="MIDDLENAME"
                    hasFeedback
                    rules={[{ required: true, message: "Please input Middlename!" }]}
                  >
                    <Input
                      placeholder="middlename"
                      defaultValue={empData.MIDDLENAME}
                    />
                  </Form.Item>
                </Col>

                <Col className="gutter-row" span={12}>
                  <label>Last Name </label>
                  <Form.Item
                    name="LASTNAME"
                    hasFeedback
                    rules={[{ required: true, message: "Please input Lastname!" }]}
                  >
                    <Input placeholder="lastname" defaultValue={empData.LASTNAME} />
                  </Form.Item>
                </Col>
                <Col className="gutter-row" span={12}>
                  <label>Gender</label>
                  <Form.Item
                    name="GENDER"
                    hasFeedback
                    rules={[
                      {
                        required: true,
                        message: "Please select Gender!",
                      },
                    ]}
                  >
                    <Select
                      placeholder="Select Gender"
                      defaultValue={empData.GENDER}
                    >
                      <Option value="male">Male</Option>
                      <Option value="female">Female</Option>
                    </Select>
                  </Form.Item>
                </Col>

                <Col className="gutter-row" span={12}>
                  <label>Careertrack</label>
                  <Form.Item
                    name="CAREERTRACK"
                    hasFeedback
                    rules={[
                      { required: true, message: "Please enter Careertrack!" },
                    ]}
                  >
                    <Input
                      placeholder="careertrack"
                      defaultValue={empData.CAREERTRACK}
                    />
                  </Form.Item>
                </Col>
                <Col className="gutter-row" span={12}>
                  <label>Mobile</label>
                  <Form.Item
                    name="MOBILENUMBER"
                    hasFeedback
                    rules={[
                      { required: true, message: "Please enter Mobile Number!" },
                      { min: 10, message: "Minimum length should be 10" },
                    ]}
                  >
                    <Input
                      type="number"
                      autoComplete="off"
                      defaultValue={empData.MOBILENUMBER}
                    />
                  </Form.Item>
                </Col>
                <Col className="gutter-row" span={12}>
                  <label>EMP Type</label>
                  <Form.Item
                    name="EMPTYPE"
                    hasFeedback
                    rules={[
                      {
                        required: true,
                        message: "Please select Employee Type!",
                      },
                    ]}
                  >
                    <Select
                      placeholder="Select Employee Type"
                      defaultValue={empData.EMPTYPE}
                    >
                      <Option value="regular">Regular</Option>
                      <Option value="contractor">Contractor</Option>
                      <Option value="intern">Intern</Option>
                    </Select>
                  </Form.Item>
                </Col>
                <Col className="gutter-row" span={12}>
                  <label>DOB</label>
                  <Form.Item
                    name="DOB"
                    rules={[
                      {
                        type: "object",
                        required: true,
                        message: "Please input your DOB",
                      },
                    ]}
                  >
                    <DatePicker />
                  </Form.Item>
                </Col>
                <Col className="gutter-row" span={12}>
                  <label>DOJ</label>
                  <Form.Item
                    name="DOJ"
                    rules={[
                      {
                        type: "object",
                        required: true,
                        message: "Please input your DOJ",
                      },
                    ]}
                  >
                    <DatePicker />
                  </Form.Item>
                </Col>
              </Row>
            </TabPane>

            <TabPane tab="Business Details" key="2">
              <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
                <Col className="gutter-row" span={12}>
                  <label>Designation </label>
                  <Form.Item
                    name="DESIGNATION"
                    hasFeedback
                    rules={[
                      {
                        required: true,
                        message: "Please select Designation!",
                      },
                    ]}
                  >
                    <Select placeholder="Select Designation">
                      <Option value="engineer">Engineer</Option>
                      <Option value="lead">Lead</Option>
                      <Option value="manager">Manager</Option>
                    </Select>
                  </Form.Item>
                </Col>
                <Col className="gutter-row" span={12}>
                  <label>Status </label>
                  <Form.Item
                    name="STATUS"
                    hasFeedback
                    rules={[
                      {
                        required: true,
                        message: "Please select Status!",
                      },
                    ]}
                  >
                    <Select placeholder="Select Status">
                      <Option value="onboarding">Onboarding</Option>
                      <Option value="offboarding">Offboarding</Option>
                      <Option value="inactive">Inactive</Option>
                    </Select>
                  </Form.Item>
                </Col>
                <Col className="gutter-row" span={12}>
                  <label>UNIT </label>
                  <Form.Item
                    name="UNIT"
                    hasFeedback
                    rules={[
                      {
                        required: true,
                        message: "Please select Unit!",
                      },
                    ]}
                  >
                    <Select placeholder="Select Unit">
                      <Option value="cis">CIS</Option>
                      <Option value="idm">IDM</Option>
                      <Option value="other">Other</Option>
                    </Select>
                  </Form.Item>
                </Col>
                <Col className="gutter-row" span={12}>
                  <label>Departname </label>
                  <Form.Item
                    name="DEPARTMENT"
                    hasFeedback
                    rules={[{ required: true, message: "Please input Departname!" }]}
                  >
                    <Input placeholder="Departname" />
                  </Form.Item>
                </Col>
                <Col className="gutter-row" span={12}>
                  <label>Project </label>
                  <Form.Item
                    name="PROJECTS"
                    hasFeedback
                    rules={[
                      {
                        required: true,
                        message: "Please select Project!",
                      },
                    ]}
                  >
                    <Select placeholder="Select Project">
                      <Option value="1">MSS</Option>
                    </Select>
                  </Form.Item>
                </Col>
                <Col className="gutter-row" span={12}>
                  <label>Manager </label>
                  <Form.Item
                    name="MANAGER"
                    hasFeedback
                    rules={[{ required: true, message: "Please enter Manager!" }]}
                  >
                    <Input placeholder="Manager" />
                  </Form.Item>
                </Col>
                <Col className="gutter-row" span={12}>
                  <label>Base Location </label>
                  <Form.Item
                    name="BASELOCATION"
                    hasFeedback
                    rules={[
                      {
                        required: true,
                        message: "Please select Base Location!",
                      },
                    ]}
                  >
                    <Select placeholder="Select Base Location">
                      <Option value="1">AAG</Option>
                      <Option value="2">Vantage 9</Option>
                      <Option value="3">adisa</Option>
                    </Select>
                  </Form.Item>
                </Col>
                <Col className="gutter-row" span={12}>
                  <label>Current Location </label>
                  <Form.Item
                    name="CURRENTLOCATION"
                    hasFeedback
                    rules={[
                      {
                        required: true,
                        message: "Please select Current Location!",
                      },
                    ]}
                  >
                    <Select placeholder="Select Current Location">
                      <Option value="1">AAG</Option>
                      <Option value="2">Vantage 9</Option>
                      <Option value="3">adisa</Option>
                    </Select>
                  </Form.Item>
                </Col>
                <Col className="gutter-row" span={12}>
                  <label>Seat No </label>
                  <Form.Item
                    name="SEATID"
                    hasFeedback
                    rules={[{ required: true, message: "Please input Seat No!" }]}
                  >
                    <Input placeholder="Seat No" />
                  </Form.Item>
                </Col>
                <Col className="gutter-row" span={12}>
                  <label>Extension No </label>
                  <Form.Item
                    name="EXTENSIONNUMBER"
                    hasFeedback
                    rules={[
                      { required: true, message: "Please input Extension No!" },
                    ]}
                  >
                    <Input placeholder="Extension No" />
                  </Form.Item>
                </Col>
              </Row>
            </TabPane>
          </Tabs>
          <Form.Item className="m-1">
            <Button type="primary" htmlType="submit">
              Update
            </Button>
          </Form.Item>
        </Form>
      </Card>
    </div>
  );
}
};

export default connect(null, { getEmployeeDetails })(EditEmployee);
