import React, { useState, useEffect } from "react";
import {
	Form,
	Card,
	Button,
	Tabs,
	DatePicker,
	Input,
	Select,
	message,
} from "antd";
import { addEmployee } from "../../../actions/employee/employeeActions";
import { connect } from "react-redux";
import { Row, Col } from "antd";
import moment from "moment";
import { Spin } from "antd";

const { TabPane } = Tabs;
const { Option } = Select;

const AddEmployee = (props) => {
	const [locationArray, setLocationArry] = useState(props.location);
	const [projectArray, setProjectArry] = useState(props.projects);
	const [spin, setSpin] = useState(false);

	const layout = {
		labelCol: {
			span: 4,
		},
		wrapperCol: {
			span: 24,
		},
	};

	useEffect(() => {
		setSpin(false);
	}, [props.messages]);

	const onFinish = (values) => {
		const newEmp = {
			...values,
			DOB: moment(values.DOB).format("YYYY-MM-DD"),
			DOJ: moment(values.DOJ).format("YYYY-MM-DD"),
		};
		setSpin(true);
		props.addEmployee(newEmp);
	};

	const onFinishFailed = (errorInfo) => {
		message.warning("fill Presonal & Business Details currectly");
	};
	const [form] = Form.useForm();
	return (
		<div>
			<h2 cla="title">Add New Employee</h2>
			<Spin spinning={spin} size="large">
				<Form
					{...layout}
					size="middle"
					name="basic"
					form={form}
					onFinish={onFinish}
					onFinishFailed={onFinishFailed}
					initialValues={{
						STATUS: "onboarding",
					}}
				>
					<Card bordered={true} className="my-3">
						<Tabs defaultActiveKey="1">
							<TabPane tab="Personal Details" key="1">
								<Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
									<Col className="gutter-row" span={12}>
										<label>First Name </label>
										<Form.Item
											name="FIRSTNAME"
											hasFeedback
											rules={[
												{
													required: true,
													message: "Please input Firstname!",
												},
											]}
										>
											<Input placeholder="firstname" />
										</Form.Item>
									</Col>
									<Col className="gutter-row" span={12}>
										<label>Middle Name </label>
										<Form.Item
											name="MIDDLENAME"
											hasFeedback
											rules={[
												{
													message: "Please input Middlename!",
												},
											]}
										>
											<Input placeholder="middlename" />
										</Form.Item>
									</Col>

									<Col className="gutter-row" span={12}>
										<label>Last Name </label>
										<Form.Item
											name="LASTNAME"
											hasFeedback
											rules={[
												{
													required: true,
													message: "Please input Lastname!",
												},
											]}
										>
											<Input placeholder="lastname" />
										</Form.Item>
									</Col>
									<Col className="gutter-row" span={12}>
										<label>Gender</label>
										<Form.Item
											name="GENDER"
											hasFeedback
											rules={[
												{
													required: true,
													message: "Please select Gender!",
												},
											]}
										>
											<Select placeholder="Select Gender">
												<Option value="male">Male</Option>
												<Option value="female">Female</Option>
											</Select>
										</Form.Item>
									</Col>

									<Col className="gutter-row" span={12}>
										<label>Mobile</label>
										<Form.Item
											name="MOBILENUMBER"
											hasFeedback
											rules={[
												{
													required: true,
													message: "Please enter Mobile Number!",
												},
												{
													min: 10,
													message: "Minimum length should be 10",
												},
											]}
										>
											<Input type="number" autoComplete="off" />
										</Form.Item>
									</Col>

									<Col className="gutter-row" span={12}>
										<label>DOB</label>
										<Form.Item
											name="DOB"
											hasFeedback
											rules={[
												{
													type: "object",
													// required: true,
													message: "Please input your DOB",
												},
											]}
										>
											<DatePicker />
										</Form.Item>
									</Col>
								</Row>
							</TabPane>

							<TabPane tab="Business Details" key="2">
								<Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
									<Col className="gutter-row" span={12}>
										<label>Careertrack</label>
										<Form.Item
											name="CAREERTRACK"
											hasFeedback
											rules={[
												{
													required: true,
													message: "Please enter Careertrack!",
												},
											]}
										>
											<Input placeholder="careertrack" />
										</Form.Item>
									</Col>
									<Col className="gutter-row" span={12}>
										<label>Departname </label>
										<Form.Item
											name="DEPARTMENT"
											hasFeedback
											rules={[
												{
													required: true,
													message: "Please input Departname!",
												},
											]}
										>
											<Input placeholder="Departname" />
										</Form.Item>
									</Col>
									<Col className="gutter-row" span={12}>
										<label>Manager </label>
										<Form.Item
											name="MANAGER"
											hasFeedback
											rules={[
												{
													required: true,
													message: "Please enter Manager!",
												},
											]}
										>
											<Input placeholder="Manager" />
										</Form.Item>
									</Col>
									<Col className="gutter-row" span={12}>
										<label>EMP Type</label>
										<Form.Item
											name="EMPTYPE"
											hasFeedback
											rules={[
												{
													required: true,
													message: "Please select Employee Type!",
												},
											]}
										>
											<Select placeholder="Select Employee Type">
												<Option value="regular">Regular</Option>
												<Option value="contractor">
													Contractor
												</Option>
												<Option value="intern">Intern</Option>
											</Select>
										</Form.Item>
									</Col>
									<Col className="gutter-row" span={12}>
										<label>Designation </label>
										<Form.Item
											name="DESIGNATION"
											hasFeedback
											rules={[
												{
													required: true,
													message: "Please select Designation!",
												},
											]}
										>
											<Select placeholder="Select Designation">
												<Option value="engineer">Engineer</Option>
												<Option value="lead">Lead</Option>
												<Option value="manager">Manager</Option>
											</Select>
										</Form.Item>
									</Col>
									<Col className="gutter-row" span={12}>
										<label>Status </label>
										<Form.Item
											name="STATUS"
											hasFeedback
											rules={[
												{
													required: true,
													message: "Please select Status!",
												},
											]}
										>
											{/* <Select placeholder="Select Status">
                      <Option value="onboarding"> <Input placeholder="careertrack" /></Option>
                      <Option value="offboarding">Offboarding</Option>
                      <Option value="inactive">Inactive</Option>
                    </Select> */}
											<Input placeholder="Status" disabled />
										</Form.Item>
									</Col>
									<Col className="gutter-row" span={12}>
										<label>UNIT </label>
										<Form.Item
											name="UNIT"
											hasFeedback
											rules={[
												{
													required: true,
													message: "Please select Unit!",
												},
											]}
										>
											<Select placeholder="Select Unit">
												<Option value="cis">CIS</Option>
												<Option value="idm">IDM</Option>
												<Option value="other">Other</Option>
											</Select>
										</Form.Item>
									</Col>

									<Col className="gutter-row" span={12}>
										<label>Project </label>
										<Form.Item
											name="PROJECTS"
											hasFeedback
											rules={[
												{
													required: true,
													message: "Please select Project!",
												},
											]}
										>
											<Select placeholder="Select project">
												{projectArray &&
													projectArray.map((data) => (
														<Option value={data.ID} key={data.ID}>
															{data.NAME ? data.NAME : ""}
														</Option>
													))}
											</Select>
										</Form.Item>
									</Col>

									<Col className="gutter-row" span={12}>
										<label>Base Location </label>
										<Form.Item
											name="BASELOCATION"
											hasFeedback
											rules={[
												{
													required: true,
													message: "Please select Base Location!",
												},
											]}
										>
											<Select placeholder="Select Base Location">
												{locationArray.map((data) => (
													<Option
														value={data.value}
														key={data.value}
													>
														{data.text}
													</Option>
												))}
											</Select>
										</Form.Item>
									</Col>
									<Col className="gutter-row" span={12}>
										<label>Current Location </label>
										<Form.Item
											name="CURRENTLOCATION"
											hasFeedback
											rules={[
												{
													required: true,
													message:
														"Please select Current Location!",
												},
											]}
										>
											<Select placeholder="Select Current Location">
												{locationArray.map((data) => (
													<Option
														value={data.value}
														key={data.value}
													>
														{data.text}
													</Option>
												))}
											</Select>
										</Form.Item>
									</Col>
									<Col className="gutter-row" span={12}>
										<label>Seat No </label>
										<Form.Item
											name="SEATID"
											hasFeedback
											rules={[
												{
													required: true,
													message: "Please input Seat No!",
												},
											]}
										>
											<Input placeholder="Seat No" />
										</Form.Item>
									</Col>
									<Col className="gutter-row" span={12}>
										<label>Extension No </label>
										<Form.Item
											name="EXTENSIONNUMBER"
											hasFeedback
											rules={[
												{
													required: true,
													message: "Please input Extension No!",
												},
											]}
										>
											<Input placeholder="Extension No" />
										</Form.Item>
									</Col>
									<Col className="gutter-row" span={12}>
										<label>DOJ</label>
										<Form.Item
											name="DOJ"
											hasFeedback
											rules={[
												{
													type: "object",
													required: true,
													message: "Please input your DOJ",
												},
											]}
										>
											<DatePicker />
										</Form.Item>
									</Col>
								</Row>
							</TabPane>
						</Tabs>
					</Card>
					<Row justify="end">
						<Form.Item className="m-1">
							<Button type="primary" htmlType="submit">
								Submit
							</Button>
						</Form.Item>
					</Row>
				</Form>
			</Spin>
		</div>
	);
};
const mapStateToProps = (state) => ({
	messages: state.messages,
	employee: state.employee,
});
export default connect(mapStateToProps, { addEmployee })(AddEmployee);
