import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { Card, Descriptions } from "antd";
import { getEmployeeDetails } from "../../../actions/employee/employeeActions";
import { connect } from "react-redux";

const ShowEmployee = (props) => {
  const [empData, setEmpData] = useState({});
  const [loading, setLoading] = useState(true);
  useEffect(() => {
    props.getEmployeeDetails(props.empid);
  }, []);

  useEffect(() => {
    if (Object.keys(props.employee.employeeDetails).length > 0) {
      if (props.employee.employeeDetails.EMPID === props.empid) {
         setEmpData(props.employee.employeeDetails);
         setLoading(false);
      }
    }
  }, [props.employee.employeeDetails]);

//   useEffect(() => {
//     setLoading(props.employee.loading);
//   }, [props.employee.loading]);

  return (
    <div>
      <Card
        title={empData.FIRSTNAME && empData.FIRSTNAME + " " + empData.LASTNAME}
        bordered={true}
        className="my-3"
        loading={loading}
      >
        <Descriptions bordered size="small" column={1}>
          <Descriptions.Item label="Emp Id:">{empData.EMPID}</Descriptions.Item>
          {empData.EMAIL && (
            <Descriptions.Item label="Email:">{empData.EMAIL}</Descriptions.Item>
          )}
          {empData.CURRENTLOCATION && (
            <Descriptions.Item label="Address:">
              {empData.CURRENTLOCATION.ADDRESS}
            </Descriptions.Item>
          )}
          {empData.UNIT && (
            <Descriptions.Item label="Unit:">{empData.UNIT}</Descriptions.Item>
          )}
          <Descriptions.Item label="Status:">{empData.STATUS}</Descriptions.Item>
          <Descriptions.Item label="Date of Joining:">
            {empData.DOJ}
          </Descriptions.Item>
          {empData.PROJECTS && (
            <Descriptions.Item label="Current Project:">
              {empData.PROJECTS.NAME}
            </Descriptions.Item>
          )}
          {empData.MANAGER && (
            <Descriptions.Item label="Manager:">
              {empData.MANAGER.FIRSTNAME + " " + empData.MANAGER.LASTNAME}
            </Descriptions.Item>
          )}
          {empData.LASTWORKINGDATE && (
            <Descriptions.Item label="Last Working Date:">
              {empData.LASTWORKINGDATE}
            </Descriptions.Item>
          )}
        </Descriptions>
      </Card>
    </div>
  );
};

ShowEmployee.propTypes = {
  getEmployeeDetails: PropTypes.func.isRequired,
  employee: PropTypes.object,
  empid: PropTypes.string.isRequired,
};

const mapStateToProps = (state) => ({
  employee: state.employee,
});

export default connect(mapStateToProps, { getEmployeeDetails })(ShowEmployee);
