import React from "react";
import PropTypes from "prop-types";
import axios from "axios";
import { connect } from "react-redux";
import { Form, Upload, Card, Button, Space } from "antd";
import Icon from "@ant-design/icons";
import sample1 from "../../../constant/sample1.xlsx";
import sample2 from "../../../constant/sample2.csv";
// import { uploadEmployeeList } from "../../../actions/employee/employeeActions";

const BulkUpload = (props) => {
  const avatarUpload = (file) => {
    console.log(file);
    const config = {
      headers: {
        "content-type": "multipart/form-data",
      },
      body: {
        file: file,
      },
    };
    axios
      .post(`/employees/bulkupload/${file.name}`, config)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };
  // const normFile = (e) => {
  //   console.log("Upload event:", e);
  //   props.dispatch(uploadEmployeeList(e.file.name));
  //   if (Array.isArray(e)) {
  //     return e;
  //   }
  //   return e && e.fileList;
  // };
  return (
    <div>
      <Card title="Upload Employee List" bordered={true} className="my-3">
        <Form layout="vertical">
          <Form.Item label="Select file to upload">
            <Upload name="avatar" action={avatarUpload} listType="picture-card">
              <p className="ant-upload-drag-icon">
                <Icon type="inbox" />
              </p>
              <p className="ant-upload-text">Clique ou arraste um arquivo.</p>
            </Upload>
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit" block>
              Upload
            </Button>
            <Space>
              <a href={sample1} download="sample_xlsx">
                Download sample xlsx
              </a>
              /
              <a href={sample2} download="sample_csv">
                Download sample csv
              </a>
            </Space>
          </Form.Item>
        </Form>
      </Card>
    </div>
  );
};

BulkUpload.propTypes = {
  dispatch: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  employee: state.employee,
});

export default connect(mapStateToProps)(BulkUpload);
