function ErrorString(message) {
  if (
    message ===
    "invalidValue, User not found. Check credentials and the specified realm."
  ) {
    return "You have entered an invalid username or password";
  }

  if (
    message ===
    "internalError User is not authorized to operate with this rest api."
  ) {
    return "User is not authorized";
  }

  if (message === "WRONG_PASSWORD") {
    return "You have entered an invalid current password";
  }
}

export default ErrorString;
