import React, { useEffect } from "react";
import { Route, Switch, Redirect } from "react-router-dom";
import { positions} from "react-alert";
import { Provider as AlertProvider } from 'react-alert'
import AlertTemplate from "react-alert-template-basic";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import "antd/dist/antd.css";
import "./App.css";
import { Layout, message } from "antd";
import Dashboard from "./components/pages/dashboard/dashboard";
import Employee from "./components/pages/employee/employee";
import PrivateRoute from "./components/utils/PrivateRoute";
import Navbar from "./components/layout/navbar/Navbar";
import Login from "./components/pages/login/Login";
import { setAuthToken, axiosResponseInterceptor } from "./components/utils/setToken";

const { Content } = Layout;

if (localStorage.accessToken) {
  setAuthToken(localStorage.accessToken);
}
axiosResponseInterceptor();
const App = (props) => {
  useEffect(() => {
    if (props.messages.success.length) {
      props.messages.success.map((msg) => message.success(msg.text));
    }
    if (props.messages.error.length) {
      props.messages.error.map((msg) => message.error(msg.text));
    }
  }, [props.messages]);
  return (
    <div className="App">
      <Navbar />
      <Route
        exact
        path="/"
        render={() =>
          props.auth.isAuthenticated ? <Redirect to="/dashboard" /> : <Login />
        }
      />
      <Layout className="container">
        <Content
          className="site-layout-background"
          style={
            props.auth.isAuthenticated
              ? {
                  padding: 24,
                  margin: 0,
                  minHeight: "90vh",
                }
              : {}
          }
        >
          <Switch>
            <PrivateRoute exact path="/dashboard" component={Dashboard} />
            <PrivateRoute exact path="/employee-list" component={Employee} />
          </Switch>
        </Content>
      </Layout>
    </div>
  );
};

App.propTypes = {
  auth: PropTypes.object.isRequired,
  messages: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
  messages: state.messages,
});

export default connect(mapStateToProps, {})(App);
