import { put, takeLatest } from "redux-saga/effects";
import {
  GET_EMPLOYEES_LIST,
  GET_SINGLE_EMPLOYEE,
  ADD_EMPLOYEE,
  ADD_EMPLOYEES_LIST,
  GET_EMPLOYEES_LIST_SUCCESS,
  GET_EMPLOYEES_LIST_FAIL,
  GET_SINGLE_EMPLOYEE_SUCCESS,
  GET_SINGLE_EMPLOYEE_FAIL,
  ADD_EMPLOYEE_SUCCESS,
  ADD_EMPLOYEES_LIST_SUCCESS,
  ADD_EMPLOYEES_LIST_FAIL,
  ADD_EMPLOYEE_FAIL,
  SET_LOADING,
  EDIT_EMPLOYEE,
  EDIT_EMPLOYEE_SUCCESS,
  EDIT_EMPLOYEE_FAIL,
  GET_PROJECTS,
  GET_LOCATIONS,
  GET_LOCATIONS_SUCCESS,
  GET_LOCATIONS_FAIL,
  GET_PROJECTS_SUCCESS,
  GET_PROJECTS_FAIL,
} from "../actions/employee/employeeTypes";
import Axios from "axios";
import { SUCCESS_MESSAGE, ERROR_MESSAGE } from "../actions/auth/authTypes";

export function* getEmployeeList({ payload }) {
  yield put({ type: SET_LOADING });
  try {
    const employeeList = yield Axios.get(`/employees/`, payload).then(
      (res) => res.data
    );
    yield put({ type: GET_EMPLOYEES_LIST_SUCCESS, payload: employeeList });
  } catch (err) {
    yield put({ type: GET_EMPLOYEES_LIST_FAIL, payload: err.response.data });
    let msg = {
      id: 1,
      text: "Failed to process request",
    };
    yield put({ type: ERROR_MESSAGE, payload: msg });
  }
  yield put({ type: SET_LOADING });
}

export function* getEmployeeDetails({ payload }) {
  yield put({ type: SET_LOADING });
  try {
    const employeeDetails = yield Axios.get(`/employees/${payload}`).then(
      (res) => res.data
    );
    yield put({ type: GET_SINGLE_EMPLOYEE_SUCCESS, payload: employeeDetails });
  } catch (err) {
    yield put({ type: GET_SINGLE_EMPLOYEE_FAIL, payload: err.response.data });
    let msg = {
      id: 1,
      text: "Failed to process request",
    };
    yield put({ type: ERROR_MESSAGE, payload: msg });
  }
  yield put({ type: SET_LOADING });
}

export function* addEmployee({ payload }) {
  yield put({ type: SET_LOADING });
  try {
    const employee = yield Axios.post("/employees/", payload).then(
      (res) => res.data
    );
    let msg = {
      id: 1,
      text: "Employee Added successfully.",
    };
    yield put({ type: ADD_EMPLOYEE_SUCCESS, payload: employee });
    yield put({ type: SUCCESS_MESSAGE, payload: msg });
  } catch (err) {
    yield put({ type: ADD_EMPLOYEE_FAIL, payload: err.response.data });
    let msg = {
      id: 1,
      text: "fill Presonal & Business Details currectly",
    };
    yield put({ type: ERROR_MESSAGE, payload: msg });
  }
  yield put({ type: SET_LOADING });
}

export function* addEmployeeList({ payload }) {
  yield put({ type: SET_LOADING });
  try {
    const employeeList = yield Axios.post(
      `/employees/bulkupload/${payload.filename}`,
      payload.formData,
      {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      }
    ).then((res) => res.data);
    let msg = {
      id: 1,
      text: "List Uploaded successfully.",
    };
    yield put({ type: SUCCESS_MESSAGE, payload: msg });
    yield put({ type: ADD_EMPLOYEES_LIST_SUCCESS, payload: employeeList });
  } catch (err) {
    const errData = [];
    err.response.data &&
      err.response.data.map((val) => {
        for (let key in val.Description) {
          errData.push({
            no: val.Record_Number + 1,
            row: key,
            msg: val.Description[key].join(),
          });
        }
        return val;
      });
    yield put({ type: ADD_EMPLOYEES_LIST_FAIL, payload: errData });
    let msg = {
      id: 1,
      text: "Failed to upload data",
    };
    yield put({ type: ERROR_MESSAGE, payload: msg });
  }
  yield put({ type: SET_LOADING });
}

export function* editEmployee({ payload }) {
  const id = payload.ID;
  payload.ID = undefined;
  yield put({ type: SET_LOADING });
  try {
    const employee = yield Axios.put(`/employees/${id}`, payload).then(
      (res) => res.data
    );
    let msg = {
      id: 1,
      text: "Updated successfully.",
    };
    yield put({ type: SUCCESS_MESSAGE, payload: msg });
    yield put({ type: EDIT_EMPLOYEE_SUCCESS, payload: employee });
  } catch (err) {
    yield put({ type: EDIT_EMPLOYEE_FAIL, payload: err.response.data });
    let msg = {
      id: 1,
      text: "Failed to update fill Presonal & Business Details currectly",
    };
    yield put({ type: ERROR_MESSAGE, payload: msg });
  }
  yield put({ type: SET_LOADING });
}

export function* getLocations() {
  try {
    const locations = yield Axios.get(`/locations`).then((res) => res.data);
    yield put({ type: GET_LOCATIONS_SUCCESS, payload: locations });
  } catch (err) {
    yield put({ type: GET_LOCATIONS_FAIL, payload: err.response.data });
  }
}

export function* getProjects() {
  yield put({ type: SET_LOADING });
  try {
    const projects = yield Axios.get(`/projects`).then((res) => res.data);
    yield put({ type: GET_PROJECTS_SUCCESS, payload: projects });
  } catch (err) {
    yield put({ type: GET_PROJECTS_FAIL, payload: err.response.data });
  }
  yield put({ type: SET_LOADING });
}

export function* employeeActionWatcher() {
  yield takeLatest(GET_EMPLOYEES_LIST, getEmployeeList);
  yield takeLatest(GET_SINGLE_EMPLOYEE, getEmployeeDetails);
  yield takeLatest(ADD_EMPLOYEE, addEmployee);
  yield takeLatest(ADD_EMPLOYEES_LIST, addEmployeeList);
  yield takeLatest(EDIT_EMPLOYEE, editEmployee);
  yield takeLatest(GET_LOCATIONS, getLocations);
  yield takeLatest(GET_PROJECTS, getProjects);
}
