import React, { useState, useEffect } from "react";
import { Table, Input, Button, Select, Modal, message } from "antd";
import { connect } from "react-redux";
import Axios from "axios";
import PropTypes from "prop-types";
import BulkUpload from "../bulkupload/BulkUpload";
import EditEmployee from "../editemployee/EditEmployee";
import ShowEmployee from "../showemployee/ShowEmployee";
import { CloudUploadOutlined, PlusCircleOutlined } from "@ant-design/icons";

import {
  getEmployeeList,
  getLocations,
} from "../../../actions/employee/employeeActions";
import { units, statusList } from "../../utils/optionsList";
const { Option } = Select;
const { Search } = Input;

const Dashboard = (props) => {
  const paginationConfig = {
    defaultPageSize: 5,
    showSizeChanger: true,
    pageSizeOptions: ["5", "10", "20", "50", "100"],
  };
  const [data, setData] = useState([]);
  const [pagination, setPagination] = useState(paginationConfig);
  const [loading, setLoading] = useState(false);
  const [searchInput, setSearchInput] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [selectModal, setSelectModal] = useState("");
  const [searchParams, setSearchParams] = useState({});
  const [showEmp, setShowEmp] = useState("");
  const [projects, setProjects] = useState(null);
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    fetch();
    props.getLocations();
    //eslint-disable-next-line
  }, []);

  useEffect(() => {
    if (props.employee.locations.length > 0) {
      let locationList = props.employee.locations.map((loc) => {
        return {
          text: loc.ADDRESS,
          value: loc.ID,
        };
      });
      setLocations(locationList);
    }
  }, [props.employee.locations]);

  useEffect(() => {
    if (props.messages.success.length > 0 && showModal) {
      handleModal(null, null);
      setSearchParams({});
      setPagination({ ...pagination, current: 1 });
    }
  }, [props.messages.success]);

  useEffect(() => {
    Axios.get("/projects").then((res) => {
      setProjects(res.data);
    });
  }, []);

  const handleModal = (name, id) => {
    setShowEmp(id);
    setSelectModal(name);
    setShowModal(!showModal);
  };

  const handleTableChange = (pagination, filters, sorter) => {
    const pager = { ...pagination };
    pager.current = pagination.current;
    setPagination(pager);
    if (sorter.field === "NAME") {
      sorter.field = "FIRSTNAME";
    }
    if (sorter.field === "EMPID") {
      sorter.field = "ID";
    }
    fetch({
      ...filters,
      results: pagination.pageSize,
      page: pagination.current,
      ordering:
        sorter.order && sorter.order === "descend"
          ? `-${sorter.field}`
          : sorter.field,
      CURRENTLOCATION: filters.CURRENTLOCATION && filters.CURRENTLOCATION.join(),
      UNIT: filters.UNIT && filters.UNIT.join(),
      STATUS: filters.STATUS && filters.STATUS.join(),
    });
  };

  const fetch = (params = {}) => {
    if (Object.keys(params).length) {
      setSearchParams({
        ...params,
        offset: params.results * (params.page - 1),
        limit: params.results,
        page: undefined,
        results: undefined,
      });
    }
  };

  useEffect(() => {
    setLoading(true);
    if (searchParams.offset === 0) {
      setPagination({ ...pagination, current: 1 });
    }
    const payload = {
      params: {
        limit: 5,
        ...searchParams,
        search: searchInput,
      },
    };
    props.getEmployeeList(payload);
    //eslint-disable-next-line
  }, [searchParams]);

  useEffect(() => {
    if (Object.keys(props.employee.employeeList).length > 0) {
      const newData = props.employee.employeeList.results.map((emp) => {
        return {
          ...emp,
          NAME: {
            FIRSTNAME: emp.FIRSTNAME,
            LASTNAME: emp.LASTNAME,
          },
        };
      });
      const paginationData = { ...pagination };
      paginationData.total = props.employee.employeeList.count;
      setData(newData);
      setPagination(paginationData);
      setLoading(false);
    }
  }, [props.employee.employeeList]);

  const columns = [
    {
      title: "Emp Id",
      dataIndex: "EMPID",
      sorter: true,
    },
    {
      title: "Name",
      dataIndex: "NAME",
      sorter: true,
      render: (NAME) => NAME.FIRSTNAME + " " + NAME.LASTNAME,
    },
    {
      title: "Email",
      dataIndex: "EMAIL",
    },
    {
      title: "Address",
      dataIndex: "CURRENTLOCATION",
      render: (CURRENTLOCATION) => CURRENTLOCATION && CURRENTLOCATION.ADDRESS,
      filters: [...locations],
    },
    {
      title: "Unit",
      dataIndex: "UNIT",
      filters: units.map(unit => {
         return{
            text: unit.name,
            value: unit.value
         }
      })
    },
    {
      title: "Status",
      dataIndex: "STATUS",
    },
    {
      title: "Action",
      dataIndex: "EMPID",
      render: (EMPID) => (
        <div>
          <Button
            type="primary"
            size="small"
            className="m-1 show-emp"
            onClick={() => handleModal("show", EMPID)}
          >
            Show
          </Button>
          <Button
            type="primary"
            size="small"
            className="m-1 edit-emp"
            onClick={() => handleModal("edit", EMPID)}
          >
            Edit
          </Button>
        </div>
      ),
    },
  ];
  const selectBefore = (
    <Select
      defaultValue=""
      style={{ width: 140 }}
      className="select-before"
      onChange={(value) =>
        setSearchParams({ ...searchParams, STATUS: value, offset: 0 })
      }
    >
      <Option value="">Filter by Status</Option>
      {statusList.map(st => (
         <Option key={st.id} value={st.value}>{st.name}</Option>
      ))}
    </Select>
  );

  return (
    <div className="my-3">
      <h3>Employees</h3>
      <div className="my-3 d-flex flex-column justify-content-between">
        <div className="w-50 w-sm-50">
          <Search
            addonBefore={selectBefore}
            placeholder="Search by Name and EmpId"
            enterButton="Search"
            size="large"
            onChange={(e) => setSearchInput(e.target.value)}
            onSearch={(value) =>
              setSearchParams({ ...searchParams, search: searchInput, offset: 0 })
            }
          />
        </div>
        <Button.Group className="my-sm-3">
          <Button
            type="primary"
            style={{ margin: "0 2px" }}
            size="large"
            onClick={() => handleModal("add")}
            id="add-emp"
          >
            <PlusCircleOutlined />
            Add Employee
          </Button>
          <Button
            type="primary"
            style={{ margin: "0 2px" }}
            size="large"
            onClick={() => handleModal("upload")}
            id="upload-emp"
          >
            <CloudUploadOutlined />
            Add Employee List
          </Button>
        </Button.Group>
      </div>
      <Table
        bordered
        columns={columns}
        rowKey={(ID) => ID}
        dataSource={data}
        pagination={pagination}
        loading={loading}
        onChange={handleTableChange}
        className="overflow-auto"
      />
      <Modal
        visible={showModal}
        onCancel={handleModal}
        footer={null}
        maskClosable={false}
      >
        {selectModal === "upload" && <BulkUpload />}
        {selectModal === "add" && locations !== null && projects !== null && (
          <EditEmployee location={locations} projects={projects} />
        )}
        {selectModal === "edit" &&
          locations !== null &&
          projects !== null &&
          showEmp !== null && (
            <EditEmployee empid={showEmp} location={locations} projects={projects} />
          )}
        {selectModal === "show" && <ShowEmployee empid={showEmp} />}
      </Modal>
    </div>
  );
};
Dashboard.propTypes = {
  employee: PropTypes.object,
  getEmployeeList: PropTypes.func.isRequired,
  getLocations: PropTypes.func.isRequired,
};
const mapStateToProps = (state) => ({
  employee: state.employee,
  messages: state.messages,
});

export default connect(mapStateToProps, { getEmployeeList, getLocations })(
  Dashboard
);
