import {
  GET_EMPLOYEES_LIST_SUCCESS,
  GET_SINGLE_EMPLOYEE_SUCCESS,
  ADD_EMPLOYEES_LIST_FAIL,
  ADD_EMPLOYEE_FAIL,
  ADD_EMPLOYEE_SUCCESS,
  SET_LOADING,
  GET_EMPLOYEES_LIST_FAIL,
  GET_SINGLE_EMPLOYEE_FAIL,
  EDIT_EMPLOYEE_FAIL,
  EDIT_EMPLOYEE_SUCCESS,
  ADD_EMPLOYEES_LIST_SUCCESS,
  GET_LOCATIONS_SUCCESS,
  GET_PROJECTS_SUCCESS,
} from "../../actions/employee/employeeTypes";

const initialState = {
  employeeList: {},
  employeeDetails: {},
  errors: [],
  loading: false,
  closeModal: false,
  locations: [],
  projects: [],
};

export default function (state = initialState, action) {
  switch (action.type) {
    case GET_EMPLOYEES_LIST_SUCCESS:
      return {
        ...state,
        employeeList: action.payload,
      };

    case GET_SINGLE_EMPLOYEE_SUCCESS:
      return {
        ...state,
        employeeDetails: action.payload,
      };

    case SET_LOADING:
      return {
        ...state,
        loading: !state.loading,
      };
    case ADD_EMPLOYEE_SUCCESS:
    case ADD_EMPLOYEES_LIST_SUCCESS:
      return {
        ...state,
        errors: [],
        closeModal: true,
      };
    case GET_LOCATIONS_SUCCESS:
      return {
        ...state,
        locations: [...action.payload],
      };
    case GET_PROJECTS_SUCCESS:
      return {
        ...state,
        projects: [...action.payload],
      };
    case EDIT_EMPLOYEE_SUCCESS:
      return {
        ...state,
        closeModal: true,
        errors: [],
        employeeDetails: action.payload,
      };
    case ADD_EMPLOYEE_FAIL:
      return {
        ...state,
        errors: [action.payload],
      };
    case EDIT_EMPLOYEE_FAIL:
    case ADD_EMPLOYEES_LIST_FAIL:
    case GET_EMPLOYEES_LIST_FAIL:
    case GET_SINGLE_EMPLOYEE_FAIL:
      return {
        ...state,
        errors: [...action.payload],
      };

    default:
      return state;
  }
}
